SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_linha_dados_medic (nr_prescricao_p bigint, ie_tipo_p text, nr_linha_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

/* ie_tipo:
	D = Dose
	V = Via Aplicação
	Du = Dose + Unidade de medida da dose
*/
BEGIN

if (ie_tipo_p = 'D') then

	select	ds_dados
	into STRICT	ds_retorno_w
	from (	SELECT	qt_dose ds_dados,
		row_number() OVER () AS linha
		from  	prescr_material
		where 	nr_prescricao  		= nr_prescricao_p
		and	ie_agrupador		= 1
		and	ie_suspenso		<> 'S'
		order by nr_agrupamento,
			 nr_sequencia) alias1
	where	linha = nr_linha_p;

elsif (ie_tipo_p = 'V') then

	select	ds_dados
	into STRICT	ds_retorno_w
	from (	SELECT	ie_via_aplicacao ds_dados,
		row_number() OVER () AS linha
		from  	prescr_material
		where 	nr_prescricao  		= nr_prescricao_p
		and	ie_agrupador		= 1
		and	ie_suspenso		<> 'S'
		order by nr_agrupamento,
			 nr_sequencia) alias1
	where	linha = nr_linha_p;

elsif (ie_tipo_p = 'DU') then

	select	ds_dados
	into STRICT	ds_retorno_w
	from (	SELECT	qt_dose || ' ' || cd_unidade_medida_dose ds_dados,
		row_number() OVER () AS linha
		from  	prescr_material
		where 	nr_prescricao  		= nr_prescricao_p
		and	ie_agrupador		= 1
		and	ie_suspenso		<> 'S'
		order by nr_agrupamento,
			 nr_sequencia) alias1
	where	linha = nr_linha_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
