SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_linha_medicamento (nr_prescricao_p bigint, nr_sequencia_solucao_p bigint, ie_tipo_p text, nr_linha_p bigint) RETURNS varchar AS $body$
DECLARE


ds_medicamento_w	varchar(255);

/* ie_tipo:
	M  = Todos medicamentos da prescrição.
	S   = Medicamentos da solução
	CS = Componentes da solução + dose e UM
*/
BEGIN

if (ie_tipo_p = 'S') then
	select	ds_material
	into STRICT	ds_medicamento_w
	from (	SELECT 	substr(obter_desc_mat_prescr(a.nr_prescricao, a.nr_sequencia, 'S', 0),1,250) ds_material,
			row_number() OVER () AS linha
			from  	material b,
				prescr_material a
			where	a.cd_material = b.cd_material
			and 	a.nr_prescricao = nr_prescricao_p
			and 	a.nr_sequencia_solucao = nr_sequencia_solucao_p
			and	a.ie_suspenso <> 'S'
			order by substr(obter_desc_mat_prescr(a.nr_prescricao, a.nr_sequencia, 'S', 0),1,250)) alias5
	where	linha = nr_linha_p;
elsif (ie_tipo_p = 'M') then
	select	ds_material
	into STRICT	ds_medicamento_w
	from (	SELECT	substr(obter_desc_material(cd_material),1,200) ds_material,
			row_number() OVER () AS linha
		from  	prescr_material
		where 	nr_prescricao  		= nr_prescricao_p
		and	ie_agrupador		= 1
		and	ie_suspenso		<> 'S'
		order by nr_agrupamento,
			 nr_sequencia) alias3
	where	linha = nr_linha_p;
elsif (ie_tipo_p = 'CS') then
	select	ds_material
	into STRICT	ds_medicamento_w
	from (	SELECT	substr(Obter_Desc_Redu_Material(a.cd_material),1,50) || '    '|| a.qt_dose || ' ' || a.cd_unidade_medida ds_material,
			row_number() OVER () AS linha
		from	prescr_material a
		where	a.nr_prescricao		= nr_prescricao_p
		and	a.nr_sequencia_solucao	= nr_sequencia_solucao_p
		and	coalesce(a.dt_suspensao::text, '') = ''
		order by ds_material) alias4
	where	linha = nr_linha_p;
end if;


return	ds_medicamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_linha_medicamento (nr_prescricao_p bigint, nr_sequencia_solucao_p bigint, ie_tipo_p text, nr_linha_p bigint) FROM PUBLIC;

