CREATE OR REPLACE FUNCTION obter_linha_texto ( ds_texto_p text, nr_linha_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000)	:= null;
ds_linha_w	varchar(4000);
ds_restante_w	varchar(4000);
nr_linha_w	bigint	:= 0;


BEGIN

if (ds_texto_p IS NOT NULL AND ds_texto_p::text <> '') and (nr_linha_p IS NOT NULL AND nr_linha_p::text <> '') then
	ds_restante_w	:= ds_texto_p;
	while(ds_restante_w IS NOT NULL AND ds_restante_w::text <> '') loop
		nr_linha_w	:= nr_linha_w + 1;
		if (position(chr(13) || chr(10) in ds_restante_w) = 0) then
			ds_linha_w	:= substr(ds_restante_w,1,4000);
			ds_restante_w	:= null;
		else
			ds_linha_w	:= substr(ds_restante_w,1,position(chr(13) || chr(10) in ds_restante_w));
			ds_restante_w	:= substr(ds_restante_w,position(chr(13) || chr(10) in ds_restante_w) + 2,4000);
		end if;
		if (nr_linha_w = nr_linha_p) then
			ds_retorno_w	:= ds_linha_w;
			exit;
		end if;
	end loop;
end if;

return	replace(replace(ds_retorno_w,chr(13),null),chr(10),null);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
