CREATE OR REPLACE FUNCTION obter_link_acesso (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
ds_linc_w 	varchar(255);
			

BEGIN 
 
select 	max(a.ds_link) 
into STRICT	ds_linc_w 
from 	link_acesso_web a, 
	link_acesso_web_lib b 
where	a.nr_sequencia = b.nr_seq_link 
and	((b.cd_perfil = obter_perfil_ativo) or (coalesce(b.cd_perfil::text, '') = '')) 
and	((b.cd_pessoa_fisica = obter_pessoa_fisica_usuario(nm_usuario_p,'C')) or (coalesce(b.cd_pessoa_fisica::text, '') = ''));
 
return	ds_linc_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
