CREATE OR REPLACE FUNCTION obter_lista_anestesia ( nr_cirurgia_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_anestesia_w	varchar(80) := null;
ds_anestesias_w	varchar(2000);

c01 CURSOR FOR
	SELECT	substr(obter_desc_tecnica_anest(nr_seq_tecnica),1,100)
	from	cirurgia_tec_anestesica
	where	nr_cirurgia = nr_cirurgia_p
	order by 1;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	ds_anestesia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ds_anestesias_w IS NOT NULL AND ds_anestesias_w::text <> '') then
		ds_anestesias_w := ds_anestesias_w || ', ';
	end if;

	ds_anestesias_w	:= ds_anestesias_w || ds_anestesia_w;

	end;
END LOOP;
CLOSE C01;

return ds_anestesias_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
