CREATE OR REPLACE FUNCTION obter_lista_atend_paciente (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000) := '';
nr_atendimento_w		bigint;


c01 CURSOR FOR
	SELECT	nr_atendimento
	from	atendimento_paciente
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	order 	by nr_atendimento desc;

				

BEGIN

open c01;
loop
fetch c01 into	
	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		if (length(ds_retorno_w ||','||nr_atendimento_w) > 2000) then
			exit;
		end if;

		ds_retorno_w := ds_retorno_w ||','||nr_atendimento_w;
	end;
end loop;
close c01;

select	substr(ds_retorno_w,2,Length(ds_retorno_w))
into STRICT	ds_retorno_w
;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
