CREATE OR REPLACE FUNCTION obter_lista_atend_suep ( cd_pessoa_fisica_p text, ie_tipo_atendimento_p bigint, qt_consultas_p bigint, ie_tipo_busca_p text default null, nr_atendimento_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000) := '';
nr_atendimento_w		bigint;
qt_resultados_w			bigint := 0;
ie_tipo_atendimento_w	bigint;
nr_seq_episodio_w		bigint;

c01 CURSOR FOR
	SELECT	nr_atendimento
	from	atendimento_paciente
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(ie_tipo_atendimento,-1) = coalesce(ie_tipo_atendimento_w,coalesce(ie_tipo_atendimento,-1))
	order 	by dt_entrada desc;

c02 CURSOR FOR
	SELECT	nr_atendimento
	from	atendimento_paciente
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(ie_tipo_atendimento,-1) = coalesce(ie_tipo_atendimento_w,coalesce(ie_tipo_atendimento,-1))
	and	nr_seq_episodio = nr_seq_episodio_w
	order 	by dt_entrada desc;



BEGIN

if (coalesce(qt_consultas_p,0) > 0) then

	if (ie_tipo_atendimento_p = -1) then
		ie_tipo_atendimento_w := null;
	end if;

	if (coalesce(nr_atendimento_p,0) > 0) then

		select	coalesce(max(nr_seq_episodio),0)
		into STRICT	nr_seq_episodio_w
		from	atendimento_paciente
		where	nr_atendimento = nr_atendimento_p;

	end if;


	if (ie_tipo_busca_p = 'P') then

		open c01;
		loop
		fetch c01 into
			nr_atendimento_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			ds_retorno_w :=  ds_retorno_w ||','||nr_atendimento_w;

			qt_resultados_w := qt_resultados_w + 1;

			if ( qt_resultados_w = qt_consultas_p ) then
				goto Fim;
			end if;

			end;
		end loop;
		close c01;

	else


		open c02;
		loop
		fetch c02 into
			nr_atendimento_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			ds_retorno_w :=  ds_retorno_w ||','||nr_atendimento_w;

			qt_resultados_w := qt_resultados_w + 1;

			if ( qt_resultados_w = qt_consultas_p ) then
				goto Fim;
			end if;

			end;
		end loop;
		close c02;

	end if;




	<<Fim>>

	select	substr(ds_retorno_w,2,Length(ds_retorno_w))
	into STRICT	ds_retorno_w
	;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
