CREATE OR REPLACE FUNCTION obter_lista_cardapio ( dt_referencia_p timestamp, cd_dieta_p bigint, cd_grupo_prod_p bigint, cd_estabelecimento_p bigint, nr_seq_servico_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  	varchar(4000);
ds_op1_w 	varchar(4000);
ds_op2_w 	varchar(4000);
ds_receita_w  	varchar(255);
ie_contador_w	bigint;


c01 CURSOR FOR
	SELECT  distinct c.ds_receita
	from    nut_receita c,
		nut_cardapio a,
		nut_cardapio_dia x
	where   a.nr_seq_receita    = c.nr_sequencia
	and     x.nr_seq_servico    = nr_seq_servico_p
	and     a.nr_seq_card_dia   = x.nr_sequencia
	and     obter_se_cardapio_dia(dt_referencia_p, x.ie_semana, x.ie_dia_semana) = 'S'
	and   	x.cd_estabelecimento = cd_estabelecimento_p
	and  (( x.cd_dieta = cd_dieta_p) or ( cd_grupo_prod_p = x.nr_seq_grupo_producao))
	order by 1;


BEGIN

ds_retorno_w := '';
ie_contador_w := 0;

begin
open c01;
loop
	fetch c01 into
	ds_receita_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

		if (ie_contador_w < 11) then
			ds_op1_w := ds_retorno_w || '( ) ' || ds_receita_w || chr(10) || chr(13);
		else
			ds_op2_w := ds_retorno_w || '( ) ' || ds_receita_w || chr(10) || chr(13);
		end if;

		ie_contador_w := ie_contador_w + 1;

		if (ie_opcao_p = 1) then
			ds_retorno_w := ds_op1_w;
		else
			ds_retorno_w := ds_op2_w;
	end if;

	end;
end loop;
close c01;
end;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
