CREATE OR REPLACE FUNCTION obter_lista_cme_monta ( nr_seq_agenda_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_conjunto_w		varchar(400);
ds_lista_conjunto_w	varchar(2000):=null;
ds_observacao_w		varchar(2000):=null;
ds_observacoes_w	varchar(2000):=null;

c01 CURSOR FOR
	--select substr(substr(cme_obter_nome_conjunto(nr_seq_conjunto),1,125) || substr('Obs: '||ds_observacao,1,200),1,400)
	--from   agenda_pac_cme
	--where  nr_seq_agenda = nr_seq_agenda_p;
	SELECT	substr(cme_obter_desc_classif_conj(nr_seq_classificacao),1,125),
		substr(ds_observacao,1,200)
	from   	agenda_monta_cme
	where  	nr_seq_agenda = nr_seq_agenda_p;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	ds_conjunto_w,
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(ds_lista_conjunto_w::text, '') = '') then
		ds_lista_conjunto_w 	:= substr(ds_conjunto_w,1,2000);
	else
		ds_lista_conjunto_w	:= substr(ds_lista_conjunto_w || ',' || ds_conjunto_w,1,2000);
	end if;
	if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
		if (coalesce(ds_observacoes_w::text, '') = '') then
			ds_observacoes_w := substr('Obs: '||ds_observacao_w,1,2000);
		else
			ds_observacoes_w := substr(ds_observacoes_w || ';'|| ds_observacao_w,1,2000);
		end if;
	end if;
	end;
END LOOP;
CLOSE C01;

if (ds_observacoes_w IS NOT NULL AND ds_observacoes_w::text <> '') then
	return substr(ds_lista_conjunto_w || ' ['||ds_observacoes_w||']',1,2000);
else
	return substr(ds_lista_conjunto_w,1,2000);
end if;

end;
$body$
