CREATE OR REPLACE FUNCTION obter_lista_contraste_procint (nr_seq_proc_interno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1000) := '';
ds_contraste_w			varchar(60);

C01 CURSOR FOR
	SELECT 	ds_contraste
	from 	proc_interno_contraste
	where 	nr_seq_proc_interno = nr_seq_proc_interno_p
	and 	ie_situacao = 'A'
	order by 1;


BEGIN


open C01;
loop
fetch C01 into
	ds_contraste_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_retorno_w := ds_retorno_w||','||ds_contraste_w;

	end;
end loop;
close C01;

ds_retorno_w := substr(ds_retorno_w,2,length(ds_retorno_w));

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
