CREATE OR REPLACE FUNCTION obter_lista_equip_monta ( nr_seq_agenda_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_equipamento_w	varchar(400);
ds_equipamentos_w	varchar(2000);
ds_observacao_w		varchar(2000):=null;
ds_observacoes_w	varchar(2000):=null;

c01 CURSOR FOR
/*
	select	substr(substr(obter_desc_classif_equip(NR_SEQ_CLASSIF_EQUIP),1,80) || 'Obs: '||substr(ds_observacao,1,200),1,400)
	from	agenda_pac_equip
	where	nr_seq_agenda	= nr_seq_agenda_p
	and	NR_SEQ_CLASSIF_EQUIP is not null
	and	IE_ORIGEM_INF = 'I'
	union all
	select	substr(substr(Obter_Desc_Equip_Agenda(cd_equipamento),1,200) || 'Obs: '||substr(ds_observacao,1,200),1,400)
	from	agenda_pac_equip
	where	nr_seq_agenda	= nr_seq_agenda_p
	and	cd_equipamento is not null
	and	IE_ORIGEM_INF = 'I'
	order by 1;
*/
	SELECT	substr(obter_desc_classif_equip(NR_SEQ_CLASSIF_EQUIP),1,80),
		substr(ds_observacao,1,200)
	from	agenda_monta_equipamento
	where	nr_seq_agenda	= nr_seq_agenda_p
	order by 1;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	ds_equipamento_w,
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ds_equipamentos_w IS NOT NULL AND ds_equipamentos_w::text <> '') then
		ds_equipamentos_w := substr(ds_equipamentos_w || ', ',1,2000);
	end if;

	if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
		if (coalesce(ds_observacoes_w::text, '') = '') then
			ds_observacoes_w := substr('Obs: '||ds_observacao_w,1,2000);
		else
			ds_observacoes_w := substr(ds_observacoes_w || ';'|| ds_observacao_w,1,2000);
		end if;
	end if;

	ds_equipamentos_w	:= substr(ds_equipamentos_w || ds_equipamento_w,1,2000);

	end;
END LOOP;
CLOSE C01;

if (ds_observacoes_w IS NOT NULL AND ds_observacoes_w::text <> '') then
	return substr(ds_equipamentos_w || ' ['||ds_observacoes_w||']' ,1,2000);
else
	return substr(ds_equipamentos_w,1,2000);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
