CREATE OR REPLACE FUNCTION obter_lista_ficha_ocorrencia (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255) := '';
nr_ficha_ocorrencia_w	bigint;

c01 CURSOR FOR 
	SELECT	nr_ficha_ocorrencia 
	from	cih_ficha_ocorrencia f, 
		atendimento_paciente a 
	where	a.nr_atendimento 	= f.nr_atendimento 
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;
			

BEGIN 
 
 
open c01;
loop 
fetch c01 into	 
	nr_ficha_ocorrencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
		 
	ds_retorno_w := ds_retorno_w || ',' || nr_ficha_ocorrencia_w;
	 
	end;
end loop;
close c01;
 
 
select	substr(ds_retorno_w,2,length(ds_retorno_w)) 
into STRICT	ds_retorno_w
;
 
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
