CREATE OR REPLACE FUNCTION obter_lista_frase_anticorpo (nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(2000) := null;
ds_frase_w	varchar(255);

c01 CURSOR FOR
	SELECT 	p.ds_frase
	from	laudo_pac_exame_compl l,
		prescr_proc_result_antic a,
		patologia_anticorpos p
	where	l.nr_seq_laudo 		= nr_seq_laudo_p
	and	l.nr_seq_exame_compl 	= a.nr_seq_prescr_proc_compl
	and	p.nr_sequencia 		= a.nr_seq_anticorpo
	order	by p.ds_anticorpo;


BEGIN


open c01;
loop
fetch c01 into
	ds_frase_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ds_retorno_w := ds_retorno_w || ds_frase_w || chr(13);

	end;
end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
