CREATE OR REPLACE FUNCTION obter_lista_inf_lab_exam ( nr_seq_suep_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000) := '';

nr_seq_item_w item_suep.nr_sequencia%type;
nr_seq_subitem_w subitem_suep.nr_sequencia%type;
nr_seq_exame_w informacao_suep.nr_seq_exame%type;

c01 CURSOR FOR
	SELECT	nr_seq_exame
	from	informacao_suep
	where	nr_seq_item = nr_seq_item_w;

c02 CURSOR FOR
  SELECT	nr_seq_exame
	from	informacao_suep
	where nr_seq_subitem = nr_seq_subitem_w;


BEGIN

select	nr_sequencia
into STRICT	nr_seq_item_w
from	item_suep
where	nr_seq_suep = nr_seq_suep_p
and		ie_tipo_item = ie_tipo_item_p;

if (ie_tipo_item_p <> 'EX') then
select	nr_sequencia
into STRICT	nr_seq_subitem_w
from	subitem_suep
where	nr_seq_item = nr_seq_item_w
and		ie_tipo_item = 'EX';
end if;

open c01;
	loop
	fetch c01 into
		nr_seq_exame_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
				ds_retorno_w :=  ds_retorno_w || ',';
			end if;
			ds_retorno_w :=  ds_retorno_w || nr_seq_exame_w;
		end;
		end loop;
	close c01;

open c02;
	loop
	fetch c02 into
		nr_seq_exame_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
			if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
				ds_retorno_w :=  ds_retorno_w || ',';
			end if;
			ds_retorno_w :=  ds_retorno_w || nr_seq_exame_w;
		end;
		end loop;
	close c02;

return	ds_retorno_w;

end;
