CREATE OR REPLACE FUNCTION obter_lista_kit_mat_rec ( cd_recomendacao_p bigint, cd_perfil_p bigint, cd_convenio_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


c01 CURSOR FOR
	SELECT 	cd_kit
	FROM    kit_mat_recomendacao
	WHERE   cd_recomendacao	= cd_recomendacao_p
	AND     ((cd_perfil = cd_perfil_p) OR (coalesce(cd_perfil::text, '') = ''))
	AND     ((cd_convenio = cd_convenio_p) OR (coalesce(cd_convenio::text, '') = ''))
	AND     ((cd_setor_atendimento = cd_setor_atendimento_p) OR (coalesce(cd_setor_atendimento::text, '') = ''))
	AND     ((coalesce(nr_seq_agrupamento::text, '') = '') OR (obter_agrupamento_class_setor(cd_setor_atendimento_p) = nr_seq_agrupamento))
	ORDER BY 	coalesce(cd_perfil,0),
				coalesce(cd_convenio,0),
				coalesce(cd_setor_atendimento,0),
				coalesce(nr_seq_agrupamento,0);

cd_kit_w			kit_mat_recomendacao.cd_kit%TYPE;
ds_lista_kit_w		varchar(4000) := NULL;


BEGIN

OPEN c01;
LOOP
FETCH c01 INTO
	cd_kit_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	IF (cd_kit_w IS NOT NULL AND cd_kit_w::text <> '') THEN
		IF (coalesce(ds_lista_kit_w::text, '') = '') THEN
			ds_lista_kit_w := SUBSTR(cd_kit_w,1,2000);
		ELSE
			ds_lista_kit_w := SUBSTR(ds_lista_kit_w || ','|| cd_kit_w,1,2000);
		END IF;
	END IF;

END LOOP;
CLOSE c01;

RETURN	ds_lista_kit_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
