CREATE OR REPLACE FUNCTION obter_lista_medico_executor (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

		
nm_medico_w		varchar(255) := null;	
ds_result_w		varchar(2000) := null;

		
C01 CURSOR FOR
	SELECT	substr(obter_nome_pf(a.cd_medico_executor),1,255) nm_medico
	from	procedimento_paciente a
	where	a.nr_atendimento = nr_atendimento_p
	and	(a.cd_medico_executor IS NOT NULL AND a.cd_medico_executor::text <> '')
	group by	a.cd_medico_executor
	order by	nm_medico;


BEGIN

open C01;
loop
fetch C01 into	
	nm_medico_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	if (length(ds_result_w || ', '|| nm_medico_w) < 2000) then
		if (ds_result_w IS NOT NULL AND ds_result_w::text <> '') then
			ds_result_w := ds_result_w || ', ';
		end if;
		ds_result_w := ds_result_w || nm_medico_w;
	end if;
	
	end;
end loop;
close C01;


return	ds_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
