CREATE OR REPLACE FUNCTION obter_lista_nivel_superior (cd_dto_objeto_p bigint) RETURNS varchar AS $body$
DECLARE

  ds_lista varchar(255);
  cd_dto_objeto_w objeto_schematic.NR_SEQ_OBJ_SUP%type;

BEGIN
  cd_dto_objeto_w := cd_dto_objeto_p;
  ds_lista := cd_dto_objeto_p;

  WHILE (cd_dto_objeto_w IS NOT NULL AND cd_dto_objeto_w::text <> '')
  LOOP
    SELECT NR_SEQ_OBJ_SUP
    INTO STRICT cd_dto_objeto_w
    FROM objeto_schematic
    WHERE nr_sequencia = cd_dto_objeto_w;
    ds_lista := to_char(cd_dto_objeto_w)||','||ds_lista;
  END LOOP;
  return ds_lista;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
