SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_lista_opme_agenda ( nr_seq_agenda_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_opme_w	varchar(255);
ds_lista_opme_w	varchar(4000);

c01 CURSOR FOR
	SELECT	substr(b.ds_material,1,80)
	from	material b,
		agenda_pac_opme a
	where	a.cd_material = b.cd_material
	and	a.nr_seq_agenda = nr_seq_agenda_p
	and	(b.cd_material IS NOT NULL AND b.cd_material::text <> '')
	order by 1;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	ds_opme_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ds_lista_opme_w IS NOT NULL AND ds_lista_opme_w::text <> '') then
		ds_lista_opme_w := substr(ds_lista_opme_w || ', ',1,4000);
	end if;

	ds_lista_opme_w	:= substr(ds_lista_opme_w || ds_opme_w,1,4000);

	end;
END LOOP;
CLOSE C01;

return ds_lista_opme_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_lista_opme_agenda ( nr_seq_agenda_p bigint ) FROM PUBLIC;

