CREATE OR REPLACE FUNCTION obter_lista_paciente_sac (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_lista_sac_w		varchar(10000);
ie_pessoa_avisar_w	varchar(255);

c01 CURSOR FOR
	SELECT	b.ds_valor_dominio
	from	valor_dominio b,
		atend_paciente_sac a
	where	a.nr_atendimento	= nr_atendimento_p
	and	cd_dominio		= 2613
	and	a.ie_pessoa_avisar	= b.vl_dominio
	order by a.ie_pessoa_avisar;

BEGIN

open c01;
loop
fetch c01 into
	ie_pessoa_avisar_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_lista_sac_w := ds_lista_sac_w || ie_pessoa_avisar_w || ';';
	end;
end loop;
close c01;

return	substr(ds_lista_sac_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
