CREATE OR REPLACE FUNCTION obter_lista_pedido_proced_sec ( nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000):= null;
ds_procedimento_w   varchar(4000):= null;

c01 CURSOR FOR
	SELECT	obter_desc_prescr_proc(cd_procedimento, ie_origem_proced, nr_seq_proc_interno) ds_procedimento
	from    agenda_lista_espera_proc
	where   nr_seq_lista = nr_seq_agenda_p;
	

BEGIN

open c01;
loop
	fetch 	c01
	into 	ds_procedimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	   if (coalesce(ds_retorno_w::text, '') = '')then
		ds_retorno_w:= ds_procedimento_w;
	   else
		ds_retorno_w := ds_retorno_w||', '||ds_procedimento_w;	
	   end if;
	
	end;
end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
