CREATE OR REPLACE FUNCTION obter_lista_prob_evolucao ( ie_evolucao_clinica_p text, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


cd_evolucao_w		bigint;
ds_retorno_w		varchar(4000) := '';


BEGIN
if (ie_evolucao_clinica_p IS NOT NULL AND ie_evolucao_clinica_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
		select	max(cd_evolucao)
		into STRICT	cd_evolucao_w
		from	evolucao_paciente
		where	cd_pessoa_fisica = cd_pessoa_fisica_p
		and		ie_evolucao_clinica = ie_evolucao_clinica_p
		and		(ds_lista_problemas IS NOT NULL AND ds_lista_problemas::text <> '')
		and	  	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and		coalesce(dt_inativacao::text, '') = '';

		if (coalesce(cd_evolucao_w,0) > 0) then
			select 	max(ds_lista_problemas)
			into STRICT	ds_retorno_w
			from	evolucao_paciente
			where	cd_evolucao = cd_evolucao_w;
		end if;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
