SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_lista_proc_conta (nr_interno_conta_p bigint, nr_doc_convenio_p text, ie_retorna_origem_p text, ie_separa_virgula_p text, ie_classificacao_p text) RETURNS varchar AS $body$
DECLARE


/* Classificacao 1-Procedimento 2-Servicos/Taxas 3-Diarias T-Todos*/

cd_procedimento_w		bigint;
ie_origem_proced_w	bigint;
ie_classificacao_w	varchar(1);
ds_lista_proc_w		varchar(2000);


C01 CURSOR FOR
	SELECT	a.cd_procedimento,
			a.ie_origem_proced,
			b.ie_classificacao
	from		procedimento_paciente a,
			procedimento b
	where		a.nr_interno_conta 	= nr_interno_conta_p
	and		a.cd_procedimento		= b.cd_procedimento
	and		a.ie_origem_proced	= b.ie_origem_proced
	and		a.nr_doc_convenio		= CASE WHEN nr_doc_convenio_p='' THEN nr_doc_convenio  ELSE nr_doc_convenio_p END
	order by	b.ie_classificacao,
			a.cd_procedimento;


BEGIN
ds_lista_proc_w	:= '';

begin
	OPEN C01;
	LOOP
	FETCH C01 	into
			cd_procedimento_w,
			ie_origem_proced_w,
			ie_classificacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
			BEGIN
			if (coalesce(Length(ds_lista_proc_w),1)	< 1989)	and (ie_classificacao_p		= 'T' 	or
				 ie_classificacao_p		= ie_classificacao_w) then
			begin
			if (ie_retorna_origem_p	= 'S')	then
				begin
				if (ie_separa_virgula_p	= 'S') then
					ds_lista_proc_w	:=
					(ds_lista_proc_w||cd_procedimento_w||
					','||ie_origem_proced_w||',');							else
					ds_lista_proc_w	:=
					(ds_lista_proc_w||cd_procedimento_w||
					ie_origem_proced_w);
				end if;
				end;
			else
				begin
				if (ie_separa_virgula_p	= 'S') then
					ds_lista_proc_w	:=
					(ds_lista_proc_w||cd_procedimento_w||',');
				else
					ds_lista_proc_w	:=
					(ds_lista_proc_w||cd_procedimento_w);
				end if;
				end;
			end if;
			end;
			end if;
			END;
	END LOOP;
	CLOSE C01;
end;

if (ie_separa_virgula_p		= 'S') 	and (Length(ds_lista_proc_w)	> 1)		then
	begin
	ds_lista_proc_w	:=
	substr(ds_lista_proc_w,1,(Length(ds_lista_proc_w) -1));
	end;
end if;

RETURN ds_lista_proc_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_lista_proc_conta (nr_interno_conta_p bigint, nr_doc_convenio_p text, ie_retorna_origem_p text, ie_separa_virgula_p text, ie_classificacao_p text) FROM PUBLIC;

