CREATE OR REPLACE FUNCTION obter_lista_proc_rotina_pasta ( nm_usuario_p text, ie_pasta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(4000);
nr_sequencia_w		bigint;
ie_rotina_uti_w		varchar(1);

c01 CURSOR FOR
SELECT	nr_sequencia,
		coalesce(ie_rotina_uti,'N')
from	w_proc_rotina_esp_t
where	nm_usuario = nm_usuario_p
and		ie_pasta = ie_pasta_p;

BEGIN
open c01;
loop
fetch c01 into	nr_sequencia_w,
				ie_rotina_uti_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (ie_rotina_uti_w = 'S') and (ie_opcao_p = 'UTI') then
		if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
			ds_retorno_w := ds_retorno_w||';'||nr_sequencia_w;
		else
			ds_retorno_w := nr_sequencia_w;
		end if;
	elsif (ie_opcao_p = 'PASTA') then
		if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
			ds_retorno_w := ds_retorno_w||';'||nr_sequencia_w;
		else
			ds_retorno_w := nr_sequencia_w;
		end if;
	end if;
	end;
end loop;
close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
