SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_lista_prof_agenda ( nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000):= '';
nm_prof_cir_w		varchar(255) := '';
nm_prof_ane_w		varchar(255) := '';
nm_prof_w		varchar(255) := '';
cd_funcao_w		profissional_agenda.cd_funcao%type;
ds_funcao_w             funcao_medico.ds_funcao%type;
ds_lista_prof_w		varchar(4000);

cProfAgePac CURSOR(nr_seq_agenda_p bigint) FOR
	SELECT	coalesce(obter_nome_medico_JS(agenda_paciente.cd_medico, 'G'), obter_nome_pessoa_fisica(cd_medico, null)) 	cirurgiao,
		obter_nome_pessoa_fisica(cd_anestesista, null) 	anestesista
	from    agenda_paciente
	where   nr_sequencia = nr_seq_agenda_p;
	
cFuncao CURSOR(nr_seq_agenda_p bigint) FOR
	SELECT  pa.cd_funcao
	from    profissional_agenda pa,
		funcao_medico fm
	where (fm.ie_anestesista_principal <> 'S' and fm.ie_cirurgiao <> 'S')
	and	pa.cd_funcao = fm.cd_funcao
	and 	pa.nr_seq_agenda = nr_seq_agenda_p
	group by pa.cd_funcao;
	
cProfAge CURSOR(p_cd_funcao bigint, nr_seq_agenda_p bigint) FOR
	SELECT	obter_nome_pessoa_fisica(pa.cd_profissional, null)
	from 	profissional_agenda pa,
		funcao_medico fm
	where (fm.ie_anestesista_principal <> 'S' and fm.ie_cirurgiao <> 'S')
	and	pa.cd_funcao = fm.cd_funcao
	and	pa.cd_funcao = p_cd_funcao
	and 	nr_seq_agenda = nr_seq_agenda_p;


BEGIN

open cProfAgePac(nr_seq_agenda_p);
loop
	fetch 	cProfAgePac
	into	nm_prof_cir_w, 
		nm_prof_ane_w;
	EXIT WHEN NOT FOUND; /* apply on cProfAgePac */
		if ((coalesce(nm_prof_cir_w, null) IS NOT NULL AND (coalesce(nm_prof_cir_w, null))::text <> '')) then
			ds_retorno_w:=	obter_desc_expressao(328095)||' ('||nm_prof_cir_w||')';
		end if;
		if ((coalesce(nm_prof_ane_w, null) IS NOT NULL AND (coalesce(nm_prof_ane_w, null))::text <> '')) then
			ds_retorno_w:=  ds_retorno_w||' '||obter_desc_expressao(858598)||': ('||nm_prof_ane_w||')';
		end if;		
end loop;
close cProfAgePac;

open cFuncao(nr_seq_agenda_p);
loop
	fetch 	cFuncao
	into	cd_funcao_w;
	EXIT WHEN NOT FOUND; /* apply on cFuncao */
	
		ds_retorno_w := ds_retorno_w||' '||obter_funcao_medico(cd_funcao_w)||': (';
		ds_lista_prof_w := null;
		open cProfAge(cd_funcao_w, nr_seq_agenda_p);
		loop	
			fetch	cProfAge
			into 	nm_prof_w;
		EXIT WHEN NOT FOUND; /* apply on cProfAge */	
		
			if (coalesce(ds_lista_prof_w::text, '') = '')then
				ds_lista_prof_w := nm_prof_w;	
			else
				ds_lista_prof_w := ds_lista_prof_w||', '||nm_prof_w;	
			end if;
			
			
	 end loop;
	 close cProfAge;
	 ds_retorno_w := ds_retorno_w||ds_lista_prof_w||')';
end loop;
close cFuncao;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_lista_prof_agenda ( nr_seq_agenda_p bigint) FROM PUBLIC;

