CREATE OR REPLACE FUNCTION obter_lista_relatorio_param ( nr_seq_relatorio_p bigint, separator_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno	varchar(4000);
separator_w varchar(10) := ';';

  parameto RECORD;

BEGIN

  if ((separator_p IS NOT NULL AND separator_p::text <> '') and length(separator_p) > 0) then
    separator_w := separator_p;
  end if;

  for parameto in (
                      SELECT a.cd_parametro ds
                      from relatorio_parametro a
                      where a.nr_seq_relatorio = nr_seq_relatorio_p
                      order by a.nr_sequencia
  ) loop
    ds_retorno := ds_retorno || parameto.ds || separator_w;
  end loop;

  return ds_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
