SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_lista_result_anticorpo ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_laudo_questao_i_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_resultado_w		varchar(255);
nr_seq_peca_w		bigint;
nr_seq_peca_sup_w	bigint;
nr_seq_peca_exame_w	bigint;

C01 CURSOR FOR
	SELECT 	substr(rpad(n.ds_anticorpo,25,' ') || rpad(obter_valor_dominio(3485,coalesce(ie_resultado,0)),20,' ') || rpad(ds_resultado,80,' '),1,255),
		p.nr_sequencia
	from   	prescr_proc_result_antic a,
		prescr_proc_exame_compl c,
		prescr_proc_peca p,
		patologia_anticorpos n
	where	a.nr_seq_prescr_proc_compl = c.nr_sequencia
	and  	c.nr_seq_prescr_proc_peca = p.nr_sequencia
	and	n.nr_sequencia = a.nr_seq_anticorpo
	and	p.nr_prescricao = nr_prescricao_p
	and	p.nr_seq_prescr = nr_seq_prescr_p
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');


BEGIN
ds_retorno_w := '';

select 	max(g.nr_seq_peca)
into STRICT	nr_seq_peca_w
from   	laudo_questao_item i,
	laudo_grupo_questao g
where  	i.nr_seq_laudo_grupo 	= g.nr_sequencia
and	i.nr_sequencia 		= nr_seq_laudo_questao_i_p;

open C01;
loop
fetch C01 into
	ds_resultado_w,
	nr_seq_peca_exame_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select	max(nr_seq_peca)
	into STRICT	nr_seq_peca_sup_w
	from	prescr_proc_peca
	where	nr_sequencia = nr_seq_peca_exame_w;

	while (nr_seq_peca_sup_w IS NOT NULL AND nr_seq_peca_sup_w::text <> '') loop
		begin

		nr_seq_peca_exame_w := nr_seq_peca_sup_w;

		select	max(nr_seq_peca)
		into STRICT	nr_seq_peca_sup_w
		from	prescr_proc_peca
		where	nr_sequencia = nr_seq_peca_exame_w;

		end;
	end loop;


	if (nr_seq_peca_exame_w = nr_seq_peca_w) then
		ds_retorno_w := ds_retorno_w || ds_resultado_w || chr(13);
	end if;

end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
