CREATE OR REPLACE FUNCTION obter_lista_servico_agenda_pep ( nr_seq_agenda_p bigint, nr_atendimento_p bigint, ie_opcao_p text default null) RETURNS varchar AS $body$
DECLARE

 
ds_servico_w	varchar(80) := null;
ds_servicos_w	varchar(2000);
ie_assinou_termo_biobanco_w	varchar(10);

c01 CURSOR FOR 
	SELECT	substr(ds_proc_exame,1,80) 
	from	proc_interno b, 
		agenda_pac_servico a 
	where	b.nr_sequencia = a.nr_seq_proc_servico 
	and	a.nr_seq_agenda	= nr_seq_agenda_p 
	order by 1;


BEGIN 
 
if (coalesce(ie_opcao_p,'S') = 'S') then 
 
OPEN C01;
LOOP 
FETCH C01 into 
	ds_servico_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	if (ds_servicos_w IS NOT NULL AND ds_servicos_w::text <> '') then	 
		ds_servicos_w := ds_servicos_w || ', ';
	end if;
 
	ds_servicos_w	:= ds_servicos_w || ds_servico_w;
 
	end;
END LOOP;
CLOSE C01;
 
else 
 
	select	max(ie_assinou_termo_biobanco) 
	into STRICT	ie_assinou_termo_biobanco_w 
	from	atendimento_paciente 
	where	nr_atendimento	= nr_atendimento_p;
 
	if (ie_assinou_termo_biobanco_w IS NOT NULL AND ie_assinou_termo_biobanco_w::text <> '') then 
		if (ds_servicos_w IS NOT NULL AND ds_servicos_w::text <> '') then	 
			ds_servicos_w := ds_servicos_w || ', ';
		end if;
		if (ie_assinou_termo_biobanco_w	= 'S') then 
			ds_servicos_w	:= ds_servicos_w||' Assinatura do termo de extração do tecido humano.';
		else 
			ds_servicos_w	:= ds_servicos_w||' Recusa do termo de extração do tecido humano.';
		end if;
		 
	end if;
end if;
 
return ds_servicos_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
