CREATE OR REPLACE FUNCTION obter_lista_servico_agenda ( nr_seq_agenda_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_servico_w	varchar(80) := null;
ds_servicos_w	varchar(2000);

c01 CURSOR FOR
	SELECT	substr(ds_proc_exame,1,80)
	from	proc_interno b,
		agenda_pac_servico a
	where	b.nr_sequencia = a.nr_seq_proc_servico
	and	a.nr_seq_agenda	= nr_seq_agenda_p
	order by 1;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	ds_servico_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ds_servicos_w IS NOT NULL AND ds_servicos_w::text <> '') then
		ds_servicos_w := substr(ds_servicos_w || ', ',1,2000);
	end if;

	ds_servicos_w	:= substr(ds_servicos_w || ds_servico_w,1,2000);

	end;
END LOOP;
CLOSE C01;

return ds_servicos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_lista_servico_agenda ( nr_seq_agenda_p bigint ) FROM PUBLIC;

