CREATE OR REPLACE FUNCTION obter_lista_string2 ( ds_string_p text, ds_separador_p text) RETURNS LISTA_VARCHAR_PCK.TABELA_VARCHAR AS $body$
DECLARE


lista_retorno_w		lista_varchar_pck.tabela_varchar;
ds_texto_w		varchar(30000);
ie_posicao_separador_w	bigint;
qt_registro_w		bigint;
ds_texto_atual_w	bigint;


BEGIN
ds_texto_w 		:= ds_string_p;
ie_posicao_separador_w 	:= position(ds_separador_p in ds_texto_w);
qt_registro_w		:= 1;

<<posicao_separador>>
-- enquanto existir o separador na string
while(ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') loop


	-- garantir que não ficará o separador no início da string
	<<limpar_separador_inicio>>
	while(substr(ds_texto_w, 1, 1) = ds_separador_p) loop
		ds_texto_w := substr(ds_texto_w, 2, length(ds_texto_w) -1);
	end loop limpar_separador_inicio;

	-- garantir que não ficará o separador no final da string
	<<limpar_separador_fim>>
	while(substr(ds_texto_w, length(ds_texto_w), 1) = ds_separador_p) loop
		ds_texto_w := substr(ds_texto_w, 1, length(ds_texto_w) -1);
	end loop limpar_separador_fim;

	-- guardar o texto na nova posição da lista
	if (ie_posicao_separador_w = 0) then
		lista_retorno_w(qt_registro_w) := substr(substr(ds_texto_w, 1, length(ds_texto_w)), 1, 30000);
		ds_texto_w := null;
	else
		lista_retorno_w(qt_registro_w) := substr(substr(ds_texto_w, 1, ie_posicao_separador_w - 1), 1, 30000);
		ds_texto_w := substr(ds_texto_w, ie_posicao_separador_w + 1, length(ds_texto_w));
	end if;

	qt_registro_w := qt_registro_w + 1;

	-- atualiza condição do while
	ie_posicao_separador_w := position(ds_separador_p in ds_texto_w);

end loop posicao_separador;

return	lista_retorno_w;

end;
