CREATE OR REPLACE FUNCTION obter_lista_tipo_acomp ( nr_atendimento_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_tipo_acompanhante_w	varchar(255);
ds_lista_tipo_acompanhante_w	varchar(2000);

c01 CURSOR FOR

	SELECT	substr(b.ds_tipo_acompanhante,1,80)
	from   	atend_tipo_acompanhante a,
		tipo_acompanhante b
	where  	a.nr_seq_tipo_acompanhante = b.nr_sequencia
	and	a.nr_atendimento = nr_atendimento_p
	order by 1;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	ds_tipo_acompanhante_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ds_lista_tipo_acompanhante_w IS NOT NULL AND ds_lista_tipo_acompanhante_w::text <> '') then
		ds_lista_tipo_acompanhante_w := substr(ds_lista_tipo_acompanhante_w || ', ',1,2000);
	end if;

	ds_lista_tipo_acompanhante_w	:= substr(ds_lista_tipo_acompanhante_w || ds_tipo_acompanhante_w,1,2000);

	end;
END LOOP;
CLOSE C01;

return ds_lista_tipo_acompanhante_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
