CREATE OR REPLACE FUNCTION obter_lista_vision_attribute (cd_view_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000) := '';
nm_Attribute_w		varchar(80);


C01 CURSOR FOR

SELECT nm_atributo from TABELA_VISAO_ATRIBUTO
where nr_sequencia = cd_view_p and  IE_OBRIGATORIO = 'S';



BEGIN

open C01;
loop
fetch C01 into
	nm_Attribute_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

  ds_retorno_w := ds_retorno_w || nm_Attribute_w  || ',';

	end;
end loop;
close C01;

ds_retorno_w := substr(ds_retorno_w, 1, Length(ds_retorno_w) - 1);

return	ds_retorno_w;

end;
$body$
