SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_localizacao_laudo (nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_localizacao_w		varchar(255) := '';
nr_seq_loc_w			bigint;


BEGIN

select	coalesce(max(nr_seq_loc),0)
into STRICT	nr_seq_loc_w
from	laudo_paciente_loc
where	nr_sequencia = nr_seq_laudo_p;

if (nr_seq_loc_w > 0) then

	select	max(coalesce(DS_VALOR_DOMINIO_CLIENTE,substr(obter_desc_expressao(CD_EXP_VALOR_DOMINIO),1,255)))
	into STRICT	ds_localizacao_w
	from	valor_dominio b,
		laudo_paciente_loc a
	where	a.ie_tipo_local = b.vl_dominio
	and	b.cd_dominio	= 138
	and	nr_sequencia = nr_seq_laudo_p
	and	nr_seq_loc = nr_seq_loc_w;

end if;

return	ds_localizacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_localizacao_laudo (nr_seq_laudo_p bigint) FROM PUBLIC;

