CREATE OR REPLACE FUNCTION obter_localizacao_mat_arvore ( cd_material_p bigint, cd_local_estoque_p bigint) RETURNS varchar AS $body$
DECLARE


cd_localizacao_w			bigint;


BEGIN


select	coalesce(max(nr_seq_estrut_mat), 0)
into STRICT	cd_localizacao_w
from	localizacao_estoque_local
where	cd_material	= cd_material_p
and	cd_local_estoque	= cd_local_estoque_p;

if (cd_localizacao_w = 0) then
	begin
	select	nr_seq_estrut_mat
	into STRICT	cd_localizacao_w
	from 	padrao_estoque_local b,
		material a
	where	a.cd_material_estoque 	= b.cd_material
	and	a.cd_material		= cd_material_p
	and	b.cd_local_estoque		= cd_local_estoque_p;
	exception
		when others then
			cd_localizacao_w	:= '';
	end;
end if;

return cd_localizacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
