CREATE OR REPLACE FUNCTION obter_local_acomodacao_palm (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w 	varchar(100);

BEGIN

select	max(substr(obter_desc_local_qt(nr_seq_local,'C'),1,60))
into STRICT	ds_retorno_w
from   	paciente_atendimento
where  	nr_atendimento 		= nr_atendimento_p
and    	trunc(dt_prevista)	= trunc(clock_timestamp())
and    	coalesce(dt_cancelamento::text, '') = '';

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
