CREATE OR REPLACE FUNCTION obter_local_processo_aprov ( nr_seq_processo_p bigint) RETURNS bigint AS $body$
DECLARE

 
 
cd_local_estoque_w		bigint;


BEGIN 
 
select	coalesce(max(a.cd_local_estoque), 0) 
into STRICT	cd_local_estoque_w 
from	ordem_compra b, 
	ordem_compra_item a 
where	a.nr_ordem_compra		= b.nr_ordem_compra 
and	a.nr_seq_aprovacao	= nr_seq_processo_p;
 
if (cd_local_estoque_w = 0) then 
	select	coalesce(max(b.cd_local_estoque),0) 
	into STRICT	cd_local_estoque_w 
	from	solic_compra b, 
		solic_compra_item a 
	where	a.nr_solic_compra		= b.nr_solic_compra 
	and	a.nr_seq_aprovacao	= nr_seq_processo_p;
end if;
 
if (cd_local_estoque_w = 0) then 
	select	coalesce(max(b.cd_local_estoque),0) 
	into STRICT	cd_local_estoque_w 
	from	requisicao_material b, 
		item_requisicao_material a 
	where	a.nr_requisicao		= b.nr_requisicao 
	and	a.nr_seq_aprovacao	= nr_seq_processo_p;
end if;
 
return cd_local_estoque_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
