CREATE OR REPLACE FUNCTION obter_local_prontuario (nr_seq_same_p bigint) RETURNS varchar AS $body$
DECLARE



ds_resultado_w			varchar(255) := '';


BEGIN

select	substr(obter_nome_setor(obter_setor_usuario(obter_usuario_pf(cd_pessoa_fisica))),1,40)
into STRICT	ds_resultado_w
from	same_prontuario_hist
where	nr_sequencia in (
	   SELECT     max(nr_sequencia)
	   from       same_prontuario_hist
	   where      nr_seq_same = nr_seq_same_p
	   and        nr_seq_operacao = 11
);

return ds_resultado_w;

end;
