CREATE OR REPLACE FUNCTION obter_local_setor_equip ( nr_seq_equipamento_p bigint, nr_seq_localizacao_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(170);
VarIeAndarBlocoLocal_w	varchar(15);
nr_seq_local_w		bigint;


BEGIN
if (coalesce(nr_seq_equipamento_p,0) > 0) then
	begin
	select	nr_seq_local
	into STRICT	nr_seq_local_w
	from	man_equipamento
	where	nr_sequencia = nr_seq_equipamento_p;
	end;
end if;

if (coalesce(nr_seq_localizacao_p,0) > 0) then
	nr_seq_local_w := nr_seq_localizacao_p;
end if;

if (coalesce(nr_seq_local_w,0) > 0) then
	begin
	VarIeAndarBlocoLocal_w := obter_param_usuario(298, 53, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, VarIeAndarBlocoLocal_w);

	if (coalesce(VarIeAndarBlocoLocal_w,'N') <> 'S') then
		begin
		select	substr(obter_desc_man_localizacao(nr_sequencia),1,80) || ' / ' ||
			substr(obter_nome_setor(cd_setor),1,100)
		into STRICT	ds_retorno_w
		from	man_localizacao
		where	nr_sequencia	= nr_seq_local_w;
		end;
	else
		begin
		select	substr( CASE WHEN substr(ds_localizacao,1,50)=' ' THEN  ''   ELSE substr(ds_localizacao||' / ',1,50) END  ||
				CASE WHEN substr(coalesce(obter_desc_bloco(nr_seq_bloco),'X'),1,50)='X' THEN ''  ELSE substr(obter_desc_bloco(nr_seq_bloco)||' / ',1,50) END  ||
				CASE WHEN substr(coalesce(obter_desc_andar(nr_seq_andar),'X'),1,50)='X' THEN ''  ELSE substr(obter_desc_andar(nr_seq_andar)||' / ',1,50) END  ||
				substr(obter_nome_setor(cd_setor),1,50),1,170)
		into STRICT	ds_retorno_w
		from	man_localizacao
		where	nr_sequencia	= nr_seq_local_w;
		end;
	end if;
	end;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
