CREATE OR REPLACE FUNCTION obter_local_tipo_baixa_prescr ( cd_material_p bigint, cd_setor_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
cd_grupo_material_w		smallint;
cd_subgrupo_w			smallint;
cd_classe_material_w		integer;
cd_perfil_w			integer := Obter_perfil_ativo;
cd_local_estoque_w		smallint;
nr_seq_tipo_baixa_w		bigint;
nr_seq_familia_w			bigint;
ie_local_barras_w			varchar(15);

/*
ie_opcao_p
M - Motivo baixa
L - Local de estoque
S - Sequencia do motivo de baixa
*/
c01 CURSOR FOR
SELECT	cd_local_estoque,
		nr_seq_tipo_baixa,
		ie_local_barras
from 	regra_loc_tp_baixa_prescr
where (coalesce(cd_material::text, '') = '' or cd_material	= cd_material_p)
and (coalesce(cd_grupo_material::text, '') = '' or cd_grupo_material	= cd_grupo_material_w)
and (coalesce(cd_subgrupo_material::text, '') = '' or cd_subgrupo_material	= cd_subgrupo_w)
and (coalesce(cd_classe_material::text, '') = '' or cd_classe_material	= cd_classe_material_w)
and (coalesce(nr_seq_familia::text, '') = '' or nr_seq_familia	= nr_seq_familia_w)
and (coalesce(cd_setor_atendimento::text, '') = '' or cd_setor_atendimento	= cd_setor_p)
and (coalesce(cd_perfil::text, '') = '' or cd_perfil	= cd_perfil_w)
and	ie_situacao = 'A'
order by coalesce(cd_setor_atendimento,0),
	coalesce(cd_perfil,0),
	coalesce(cd_material,0),
	coalesce(cd_classe_material,0),
	coalesce(cd_subgrupo_material,0),
	coalesce(cd_grupo_material,0),
	coalesce(nr_seq_familia,0);


BEGIN

select	max(cd_grupo_material),
		max(cd_subgrupo_material),
		max(cd_classe_material),
		max(nr_seq_familia)
into STRICT	cd_grupo_material_w,
		cd_subgrupo_w,
		cd_classe_material_w,
		nr_seq_familia_w
from	estrutura_material_v
where	cd_material	= cd_material_p;

open C01;
loop
fetch C01 into
	cd_local_estoque_w,
	nr_seq_tipo_baixa_w,
	ie_local_barras_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	null;
	end;
end loop;
close C01;

if (ie_opcao_p = 'S') then
	ds_retorno_w := nr_seq_tipo_baixa_w;
elsif (ie_opcao_p = 'M') then
	select	cd_tipo_baixa
	into STRICT	ds_retorno_w
	from	tipo_baixa_prescricao
	where	nr_sequencia = nr_seq_tipo_baixa_w;
elsif (ie_opcao_p = 'L') then
	ds_retorno_w	:= cd_local_estoque_w;

	if (coalesce(ie_local_barras_w,'N') = 'S') and (coalesce(cd_local_estoque_w,0) = 0) then
		ds_retorno_w    := 'B';         /*Exige barras*/
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
