CREATE OR REPLACE FUNCTION obter_local_trabalho_pf (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_local_w	varchar(255);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select 	max(a.ds_local)
	into STRICT	ds_local_w
	from   	local_trabalho a,
		pessoa_fisica_loc_trab b
	where  	a.nr_sequencia = b.nr_seq_local
	and    	b.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.ie_situacao = 'A';
end if;

return	ds_local_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
