CREATE OR REPLACE FUNCTION obter_local_transfusao_cpoe (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(4000) := null;


BEGIN

select a.ds_setor_atendimento ds
into STRICT ds_retorno_w
from cpoe_hemoterapia h,
    prescr_solic_bco_sangue bc,
    setor_atendimento a,
    san_producao p,
    san_transfusao t
where h.nr_sequencia = bc.nr_seq_hemo_cpoe
and bc.nr_prescricao = t.nr_prescricao
and t.nr_sequencia = p.nr_seq_transfusao
and p.nr_sequencia = nr_sequencia_p
and h.cd_setor_tranfusao = a.cd_setor_atendimento;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
