SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_loc_atend_geap (nr_atendimento_p bigint, cd_setor_item_p bigint, nr_prescricao_p bigint, dt_entrada_unidade_p timestamp, dt_saida_unidade_p timestamp, nr_interno_conta_p bigint, cd_cgc_convenio_p text, cd_tipo_acomodacao_p bigint) RETURNS bigint AS $body$
DECLARE

 
cd_local_atend_w		bigint;
cd_setor_prescr_w		bigint;
cd_classif_setor_w		varchar(02);
nr_seq_atepacu_w		bigint;
cd_unidade_basica_w		varchar(010);
cd_unidade_compl_w		varchar(010);
cd_setor_atendimento_w		bigint;
cd_tipo_acomodacao_w		smallint;
ie_tipo_atendimento_w		smallint;
dt_prescricao_w			timestamp;
ie_classificacao_w		varchar(2);
ie_tipo_guia_w			varchar(2);
cd_tipo_acom_w			smallint;


BEGIN 
 
cd_local_atend_w	:= 0;
 
select	coalesce(max(ie_tipo_atendimento),0) 
into STRICT	ie_tipo_atendimento_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
select	coalesce(max(ie_tipo_guia),'X') 
into STRICT	ie_tipo_guia_w 
from	atend_categoria_convenio 
where	nr_atendimento = nr_atendimento_p;
 
/* DAY CLINIC */
 
 
if (ie_tipo_guia_w	= 'H') then 
	cd_local_atend_w	:= 13;
end if;
 
/* Ambulatorio */
 
 
if (ie_tipo_guia_w	= 'A') then 
	cd_local_atend_w	:= 4;
end if;
 
 
/* Local de atendimento diferente de internados */
 
 
if (ie_tipo_atendimento_w	<> 1) then 
	cd_local_atend_w	:= 4;
end if;
 
 
 
/* Local de atendimento de internados */
 
if (ie_tipo_atendimento_w	= 1) and (cd_local_atend_w = 0) then 
	BEGIN 
 
	if (coalesce(nr_prescricao_p,0) > 0) then 
		select	coalesce(max(cd_setor_atendimento),cd_setor_item_p), 
			coalesce(max(dt_prescricao),clock_timestamp()) 
		into STRICT	cd_setor_prescr_w, 
			dt_prescricao_w 
		from	prescr_medica 
		where	nr_prescricao		= nr_prescricao_p;
 
		select	cd_classif_setor 
		into STRICT	cd_classif_setor_w 
		from	setor_atendimento 
		where	cd_setor_atendimento	= cd_setor_prescr_w;
 
 
		if (cd_classif_setor_w = 4) then 
			cd_local_atend_w	:= 9;	
		elsif (cd_classif_setor_w	= '3') then 
			cd_local_atend_w	:= 8;
		elsif (cd_classif_setor_w	= '2') then 
			cd_local_atend_w	:= 0;
		end if;
 
	end if;
		select	cd_classif_setor 
		into STRICT	cd_classif_setor_w 
		from	setor_atendimento 
		where	cd_setor_atendimento	= cd_setor_item_p;
 
 
	/* LOcais Fixos */
 
 
	if (cd_setor_item_p	= 90) then	 
		cd_local_atend_w	:= 99;
	end if;
 
 
	if (cd_setor_item_p	= 86) then	 
		cd_local_atend_w	:= 9;
	end if;
 
	if (cd_setor_item_p	= 7) then	 
		cd_local_atend_w	:= 8;
	end if;
 
	if (cd_tipo_acomodacao_p	= '1') then 
		cd_local_atend_w	:= 5;	
	end if;
 
	if (cd_classif_setor_w	= '4') or 
		(cd_classif_setor_w	<> '3' AND cd_tipo_acomodacao_p	= '6') then 
		cd_local_atend_w	:= 9;	
	end if;
 
	if (cd_local_atend_w	= 0) and (cd_tipo_acomodacao_p	<> 1) then 
		BEGIN 
		nr_seq_atepacu_w := 0;
 
		select 	max(a.cd_unidade_basica), 
					max(a.cd_unidade_compl), 
					max(a.cd_setor_atendimento), 
					max(d.cd_classif_setor), 
					max(c.ie_classificacao), 
				max(c.cd_tipo_acomodacao) 
		into STRICT	cd_unidade_basica_w,	 
			cd_unidade_compl_w, 
			cd_setor_atendimento_w, 
			cd_classif_setor_w, 
			ie_classificacao_w, 
				cd_tipo_acom_w 
		from	setor_atendimento d, 
			tipo_acomodacao c, 
			unidade_atendimento b, 
			atend_paciente_unidade a 
		where	a.cd_unidade_basica	= b.cd_unidade_basica 
		and	a.cd_unidade_compl	= b.cd_unidade_compl 
		and   a.cd_setor_atendimento = b.cd_setor_atendimento 
		and	b.cd_tipo_acomodacao	= c.cd_tipo_acomodacao 
		and	a.cd_setor_atendimento	= d.cd_setor_atendimento 
		and	a.cd_setor_atendimento	= cd_setor_item_p 
		and	a.nr_atendimento	= nr_atendimento_p 
		and	a.dt_entrada_unidade	= dt_entrada_unidade_p 
		and	a.dt_saida_unidade	= dt_saida_unidade_p;
 
 
		if (cd_setor_atendimento_w = 111) then 
			cd_local_atend_w	:= 10;
		elsif (cd_setor_atendimento_w = 8) and (cd_tipo_acom_w 	= 0) then 
			cd_local_atend_w	:= 8;
		elsif (cd_setor_atendimento_w = 644) and (cd_tipo_acom_w 	= 14) then 
			cd_local_atend_w	:= 8;
		elsif (cd_setor_atendimento_w = 15) and (cd_tipo_acom_w 	= 0) then 
			cd_local_atend_w	:= 8;
		elsif (ie_classificacao_w	= '1') then 
			cd_local_atend_w	:= 5;
		elsif (ie_classificacao_w	= '3') then 
			cd_local_atend_w	:= 8;
		elsif (ie_classificacao_w	= '5') then 
			cd_local_atend_w	:= 8;
		elsif (cd_setor_atendimento_w = 19) or (ie_classificacao_w	= '8') then 
			begin 
 
			select	max(a.nr_seq_interno) 
			into STRICT	nr_seq_atepacu_w 
			from	setor_atendimento d, 
				tipo_acomodacao c, 
				unidade_atendimento b, 
				atend_paciente_unidade a 
			where	a.cd_unidade_basica	= b.cd_unidade_basica 
			and	a.cd_unidade_compl	= b.cd_unidade_compl 
			and   a.cd_setor_atendimento = b.cd_setor_atendimento 
			and	b.cd_tipo_acomodacao	= c.cd_tipo_acomodacao 
			and	a.cd_setor_atendimento	= d.cd_setor_atendimento 
			and	a.nr_atendimento	= nr_atendimento_p 
			and	a.dt_saida_unidade	<= dt_entrada_unidade_p	 
			and	((d.cd_classif_setor	= 3) or (a.cd_setor_atendimento = 111));
		 
			select 	max(a.cd_unidade_basica), 
					max(a.cd_unidade_compl), 
					max(a.cd_setor_atendimento), 
					max(d.cd_classif_setor), 
					max(c.ie_classificacao), 
				max(c.cd_tipo_acomodacao) 
			into STRICT	cd_unidade_basica_w,	 
				cd_unidade_compl_w, 
				cd_setor_atendimento_w, 
				cd_classif_setor_w, 
				ie_classificacao_w, 
				cd_tipo_acom_w 
			from	setor_atendimento d, 
				tipo_acomodacao c, 
				unidade_atendimento b, 
				atend_paciente_unidade a 
			where	a.cd_unidade_basica	= b.cd_unidade_basica 
			and	a.cd_unidade_compl	= b.cd_unidade_compl 
			and   a.cd_setor_atendimento = b.cd_setor_atendimento 
			and	b.cd_tipo_acomodacao	= c.cd_tipo_acomodacao 
			and	a.cd_setor_atendimento	= d.cd_setor_atendimento		 
			and	a.nr_seq_interno	= nr_seq_atepacu_w;
 
			if (cd_setor_atendimento_w = 644) and (cd_tipo_acom_w 	<> 14) then 
				cd_local_atend_w	:= 5;
			elsif (cd_setor_atendimento_w = 8) and (cd_tipo_acom_w 	= 0) then 
				cd_local_atend_w	:= 8;
			elsif (cd_setor_atendimento_w = 644) and (cd_tipo_acom_w 	= 14) then 
				cd_local_atend_w	:= 8;
			elsif (cd_setor_atendimento_w = 15) and (cd_tipo_acom_w 	= 0) then 
				cd_local_atend_w	:= 8;
			elsif (cd_setor_atendimento_w = 111) then 
				cd_local_atend_w	:= 10;
			elsif (ie_classificacao_w	= '1') then 
				cd_local_atend_w	:= 5;
			elsif (ie_classificacao_w	= '3') then 
				cd_local_atend_w	:= 8;
			elsif (ie_classificacao_w	= '5') then 
				cd_local_atend_w	:= 8;
			else 
				cd_local_atend_w	:= 8;	
			end if;
			 
 
			end;
		else 
			begin 
 
			select	coalesce(max(8),0) 
			into STRICT	cd_local_atend_w 
			from	atend_paciente_unidade 
			where	nr_atendimento	= nr_atendimento_p 
			and	cd_setor_atendimento	= 117;
 
			end;
		end if;
 
		if (cd_local_atend_w	= 0) 	and (cd_classif_setor_w	= '3') 	then 
			cd_local_atend_w	:= 8;
		end if;
		END;
	end if;
	END;
end if;
 
if (cd_local_atend_w	= 0) 	then 
	begin 
 
	select	ie_classificacao 
	into STRICT	ie_classificacao_w 
	from	tipo_acomodacao 
	where	cd_tipo_acomodacao	= cd_tipo_acomodacao_p;
 
	if (ie_classificacao_w	= '1') then 
		cd_local_atend_w	:= 5;
	elsif (ie_classificacao_w	= '3') then 
		cd_local_atend_w	:= 8;
	elsif (ie_classificacao_w	= '5') then 
		cd_local_atend_w	:= 8;
	elsif (ie_classificacao_w	= '8') then 
		cd_local_atend_w	:= 5;
	end if;
 
	end;
end if;
 
 
if (cd_setor_item_p = 111) then 
	cd_local_atend_w	:= 10;
end if;
 
return	coalesce(cd_local_atend_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_loc_atend_geap (nr_atendimento_p bigint, cd_setor_item_p bigint, nr_prescricao_p bigint, dt_entrada_unidade_p timestamp, dt_saida_unidade_p timestamp, nr_interno_conta_p bigint, cd_cgc_convenio_p text, cd_tipo_acomodacao_p bigint) FROM PUBLIC;

