SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_loc_tnm ( nr_seq_tipo_p bigint, ie_topo_categoria_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_topo_categoria_p
t - topografia
c - categoria
*/
/*
ie_opcao_p
c-  c√≥digo
*/
nr_seq_localizacao_w	bigint;
nr_seq_loc_w		bigint;
nr_seq_sub_loc_w	bigint;


BEGIN

if (ie_topo_categoria_p = 'T') then

	select 	max(nr_seq_loc),
		max(nr_seq_sub_loc)
	into STRICT	nr_seq_loc_w,
		nr_seq_sub_loc_w
	from	can_tnm_loc_topografia
	where 	nr_sequencia = nr_seq_tipo_p;


	if (nr_seq_loc_w IS NOT NULL AND nr_seq_loc_w::text <> '') then

		nr_seq_localizacao_w	:= nr_seq_loc_w;

	elsif (nr_seq_sub_loc_w IS NOT NULL AND nr_seq_sub_loc_w::text <> '') then

		select	max(nr_seq_localizacao)
		into STRICT	nr_seq_localizacao_w
		from	can_tnm_sub_localizacao a
		where	a.nr_sequencia = nr_seq_sub_loc_w;

	end if;

elsif (ie_topo_categoria_p = 'C') then

	select 	max(nr_seq_loc),
		max(nr_seq_sub_loc)
	into STRICT	nr_seq_loc_w,
		nr_seq_sub_loc_w
	from	can_tnm_loc_categoria
	where 	nr_sequencia = nr_seq_tipo_p;

	if (nr_seq_loc_w IS NOT NULL AND nr_seq_loc_w::text <> '') then

		nr_seq_localizacao_w	:= nr_seq_loc_w;

	elsif (nr_seq_sub_loc_w IS NOT NULL AND nr_seq_sub_loc_w::text <> '') then

		select	max(nr_seq_localizacao)
		into STRICT	nr_seq_localizacao_w
		from	can_tnm_sub_localizacao a
		where	a.nr_sequencia = nr_seq_sub_loc_w;

	end if;

end if;

return	nr_seq_localizacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
