SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_log_retorno_sms ( nr_seq_agenda_p bigint, cd_pessoa_fisica_p text, dt_agenda_p timestamp, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(512);
nr_celular_w			varchar(60);
qt_dias_retorno_sms_w		bigint;


BEGIN 
 
select	coalesce(max(a.qt_dias_retorno_sms),7) 
into STRICT	qt_dias_retorno_sms_w 
from	agenda a 
where	a.cd_agenda = cd_agenda_p;
 
 
select	'55'||max(a.nr_ddd_celular)||max(a.nr_telefone_celular) 
into STRICT	nr_celular_w 
from	pessoa_fisica a 
where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
 
if (nr_celular_w IS NOT NULL AND nr_celular_w::text <> '') then 
	 
	select max(a.ds_resposta) 
	into STRICT	ds_retorno_w 
	from	log_retorno_sms a 
	where	a.nr_celular = nr_celular_w 
	and	a.dt_resposta between trunc(dt_agenda_p) and trunc(dt_agenda_p) + qt_dias_retorno_sms_w;
	 
	 
end if;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
