CREATE OR REPLACE FUNCTION obter_lookup_sv_wtela ( nm_tabela_p text, nm_atributo_p text, ds_valores_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w      varchar(2000);

BEGIN
ds_retorno_w:= ds_valores_p;
if (upper(nm_tabela_p)    = 'ATENDIMENTO_MONIT_RESP')
and (upper(nm_atributo_p) = 'CD_MOD_VENT') then
   ds_retorno_w:= ' select cd_modalidade cd, ds_modalidade ds ' 
         	          || ' from   modalidade_ventilatoria '
               	          || ' where  ie_situacao = ''A'' '
                          || ' order by nvl(nr_seq_apres,999), 2';
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
