SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_lotes_agrupamento ( nr_sequencia_p bigint, ie_agrupamento_p text) RETURNS varchar AS $body$
DECLARE

					
ds_lotes_w varchar(2000);

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	ap_lote
	where 	nr_lote_agrupamento = nr_sequencia_p;
					
BEGIN

if (coalesce(ie_agrupamento_p,'N') = 'S') then
	for vet in c01 loop
		ds_lotes_w := ds_lotes_w || ', ' || vet.nr_sequencia;
	end loop;
end if;

return ds_lotes_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
