CREATE OR REPLACE FUNCTION obter_lotes_audit_rec (nr_interno_conta_p bigint, cd_autorizacao_p text) RETURNS varchar AS $body$
DECLARE


ds_lista_lote_w		varchar(4000);
nr_seq_lote_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	lote_audit_recurso a
where	exists (select	1
		from	conta_paciente_retorno y,
			conta_paciente_ret_hist x
		where	x.nr_seq_conpaci_ret	= y.nr_sequencia
		and	x.nr_seq_lote_recurso	= a.nr_sequencia
		and	y.nr_interno_conta	= nr_interno_conta_p
		and	y.cd_autorizacao	= cd_autorizacao_p);

BEGIN

ds_lista_lote_w	:= '';
open c01;
loop
fetch c01 into
	nr_seq_lote_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (coalesce(ds_lista_lote_w::text, '') = '') then
		ds_lista_lote_w	:= nr_seq_lote_w;
	else
		ds_lista_lote_w	:= ds_lista_lote_w || ', ' || nr_seq_lote_w;
	end if;
end loop;
close c01;

return	ds_lista_lote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
