CREATE OR REPLACE FUNCTION obter_lotes_desdobrados ( nr_seq_Lote_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_lote_w			bigint;
ds_lotes_w			varchar(400):= '';

c01 CURSOR FOR
SELECT	nr_sequencia
from	ap_lote
where	nr_seq_lote_sup = nr_seq_lote_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_lote_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_lotes_w	:= ds_lotes_w || nr_seq_lote_w	 || ';';
end loop;
close c01;

return substr(ds_lotes_w,1,length(ds_lotes_w)-1);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
