CREATE OR REPLACE FUNCTION obter_lote_audit_hist_guia ( nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE

vl_saldo		double precision;


BEGIN
select	coalesce(sum(obter_valores_guia_grc(x.nr_Seq_lote_hist,x.nr_interno_conta,x.cd_autorizacao,'VP')),0)
   						+ coalesce(sum(obter_valores_guia_grc(x.nr_Seq_lote_hist,x.nr_interno_conta,x.cd_autorizacao,'VG')),0)
into STRICT  vl_saldo
from	lote_audit_hist_guia x
where	x.nr_interno_conta = nr_interno_conta_p;

return vl_saldo;

end;
$body$
