CREATE OR REPLACE FUNCTION obter_lote_dctf (nr_seq_darf_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_lote_w	bigint;
ds_lote_w	varchar(255);
ds_lotes_w	varchar(255);

c01 CURSOR FOR
	SELECT	a.nr_seq_lote
	from	dctf_lote_darf a,
		darf b
	where	a.nr_seq_darf  = b.nr_sequencia
	and	b.nr_sequencia	= nr_seq_darf_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_lote_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	select	substr(ds_lote,1,255)
	into STRICT	ds_lote_w
	from	dctf_lote
	where	nr_sequencia	= nr_seq_lote_w;

	ds_lotes_w := substr(ds_lotes_w || ds_lote_w || ', ',1,254);
end loop;
close c01;

ds_lotes_w := substr(ds_lotes_w,1,(length(ds_lotes_w) -2));

return	substr(ds_lotes_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
