CREATE OR REPLACE FUNCTION obter_lote_desconto_folha ( nr_seq_mensalidade_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		bigint;

BEGIN

select	max(a.nr_sequencia)
into STRICT	ds_retorno_w
from	cobranca_escritural	a,
	titulo_receber_cobr	b
where	a.nr_sequencia = b.nr_seq_cobranca
and	b.nr_seq_mensalidade = nr_seq_mensalidade_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
